import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import ImageAnalysisLoading from '../components/ImageAnalysisLoading';

const ImageAnalysisContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 2rem;
`;

const UploadedImage = styled.img`
  max-width: 500px;
  max-height: 300px;
  border-radius: 8px;
  margin-bottom: 2rem;
`;

const DetectedDetails = styled.div`
  margin-top: 2rem;
  padding: 1rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.radii.md};
  background-color: ${({ theme }) => theme.colors.card};
  color: ${({ theme }) => theme.colors.text};
`;

const ButtonContainer = styled.div`
  display: flex;
  gap: 1rem;
  margin-top: 2rem;
`;

const Button = styled.button`
  padding: 0.75rem 1.5rem;
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${({ theme }) => theme.radii.sm};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

function ImageAnalysis() {
  const location = useLocation();
    const navigate = useNavigate();
  const images = location.state?.images || [];
  const [loading, setLoading] = useState(true);
  const [vehicleDetails, setVehicleDetails] = useState(null);

  useEffect(() => {
    // Simulate image analysis
    setTimeout(() => {
      setLoading(false);
      setVehicleDetails({
        make: 'Toyota',
        model: 'Camry',
        year: '2020',
        condition: 'Good',
      });
    }, 2000);
  }, []);

    const handleStartNew = () => {
        navigate('/');
    };

    const handlePriceEstimate = () => {
        navigate('/results', { state: { vehicleDetails } });
    };

  return (
    <ImageAnalysisContainer>
      <h1>Image Analysis</h1>
      {loading ? (
        <ImageAnalysisLoading />
      ) : (
        <>
          {images.length > 0 && (
            <UploadedImage
              src={URL.createObjectURL(images[0])}
              alt="Uploaded Vehicle"
            />
          )}
          <DetectedDetails>
            <h3>Detected Vehicle Details:</h3>
            <p>Make: {vehicleDetails.make}</p>
            <p>Model: {vehicleDetails.model}</p>
            <p>Year: {vehicleDetails.year}</p>
            <p>Condition: {vehicleDetails.condition}</p>
          </DetectedDetails>
            <ButtonContainer>
                <Button onClick={handleStartNew}>Start New</Button>
                <Button onClick={handlePriceEstimate}>Price Estimate</Button>
            </ButtonContainer>
        </>
      )}
    </ImageAnalysisContainer>
  );
}

export default ImageAnalysis;
