import React from 'react';
import { useAppContext } from '../context/AppContext';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
// eslint-disable-next-line no-unused-vars
import ImageUpload from '../components/ImageUpload';
import VehicleForm from './VehicleForm';
import Header from '../components/Header';

const HomeContainer = styled.div`
  max-width: 800px;
  margin: 2rem auto;
  display: flex;
  flex-direction: column;
  align-items: center !important; /* Force centering with !important */
  text-align: center;
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.colors.textPrimary};
  font-size: 2.5rem;
  margin-bottom: 1.5rem;
`;

const Subtitle = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  margin-bottom: 2rem;
`;


const Home = () => {
  const navigate = useNavigate();

// eslint-disable-next-line no-unused-vars
  const handleImageUpload = (file) => {
   navigate('/loading', { state: { images: file } });
  };

  const handleFormSubmit = (formData) => {
    navigate('/results', { state: { vehicleDetails: formData } });
  };

  const { uploadedImages } = useAppContext();

  return (
    <>
      <Header />
      <HomeContainer>
        <Title>Get Your Vehicle's Value</Title>
        <Subtitle>Upload a photo or enter details manually</Subtitle>
        {uploadedImages.length > 0 && (
          <div style={{ display: 'flex', overflowX: 'auto', marginBottom: '1rem' }}>
            {uploadedImages.map((image, index) => (
              <img key={index} src={image} alt={`Uploaded Vehicle ${index}`} style={{ width: '100px', height: '100px', marginRight: '10px' }} />
            ))}
          </div>
        )}
        <VehicleForm onSubmit={handleFormSubmit} />
      </HomeContainer>
    </>
  );
};

export default Home;
