import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import { ReactComponent as UploadIcon } from '../assets/icons/upload.svg';
import { ReactComponent as CameraIcon } from '../assets/icons/camera.svg';

const ImageUploadContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding: 2rem;
  border: 2px dashed ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.radii.lg};
  background-color: ${({ theme }) => theme.colors.card};
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
    background-color: ${({ theme }) => theme.colors.background};
  }
`;

const UploadText = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 1rem;
  margin: 0;
`;

const PreviewImage = styled.img`
  max-width: 100%;
  max-height: 300px;
  border-radius: ${({ theme }) => theme.radii.md};
`;


const ImageUpload = ({ onImageUpload }) => {
  const navigate = useNavigate();
  const [previews, setPreviews] = useState([]);

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    const newPreviews = [];
    const uploadedFiles = [];

    files.forEach(file => {
      const reader = new FileReader();
      reader.onloadend = () => {
        newPreviews.push(reader.result);
        if (newPreviews.length === files.length) {
          setPreviews(newPreviews);
          onImageUpload(uploadedFiles);
        }
      };
      reader.readAsDataURL(file);
      uploadedFiles.push(file);
    });
  };

  return (
    <label>
      <input
        type="file"
        accept="image/*"
        onChange={handleImageChange}
        style={{ display: 'none' }}
        multiple // Allow multiple files
      />
      <ImageUploadContainer>
        {previews.length > 0 ? (
          <CarouselContainer>
            {previews.map((preview, index) => (
              <CarouselImage key={index} src={preview} alt={`Vehicle preview ${index + 1}`} />
            ))}
          </CarouselContainer>
        ) : (
          <>
            <UploadIcon width={48} height={48} fill="#2563eb" />
            <UploadText>Upload vehicle photos</UploadText>
            <CameraIcon width={24} height={24} fill="#64748b" />
          </>
        )}
      </ImageUploadContainer>
    </label>
  );
};


const CarouselContainer = styled.div`
  display: flex;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  -webkit-overflow-scrolling: touch;
  gap: 1rem;
  role: region; /* Add role="region" for accessibility */
`;

const CarouselImage = styled(PreviewImage)`
  scroll-snap-align: start;
  width: 100%; /* Adjust as needed */
  height: auto;
`;

export default ImageUpload;
