import React, { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import ImageAnalysisLoading from '../components/ImageAnalysisLoading';
import FinalLoading from '../components/FinalLoading';

function Loading() {
  const navigate = useNavigate();
  const location = useLocation();
  let loadingType;

  useEffect(() => {
    const vehicleDetails = location.state?.vehicleDetails || {};
    const images = location.state?.images || [];
    loadingType = location.state?.loadingType || 'initial';
    // Simulate loading process
    let destination = '/analyze';
    if (loadingType === 'final') {
      destination = '/results';
    }

    setTimeout(() => {
      navigate(destination, { state: { vehicleDetails, images } });
    }, 2000);
  }, [navigate, location.state?.vehicleDetails, location.state?.images, location.state?.loadingType]);

  let loadingText = "Analyzing your vehicle image...";
  let loadingColor = "blue";

  if (loadingType === 'final') {
    loadingText = "Estimating Vehicle Value...";
    loadingColor = "green";
  }

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      {loadingType === 'final' ? <FinalLoading loadingText={loadingText} color={loadingColor} /> : <ImageAnalysisLoading />}
    </div>
  );
}

export default Loading;
