import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import ImageUpload from '../components/ImageUpload';
import VehicleForm from './VehicleForm';
import Header from '../components/Header';
import { ReactComponent as EditIcon } from '../assets/icons/edit.svg';

const HomeContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.colors.text};
  text-align: center;
  margin-bottom: 1rem;
`;

const Subtitle = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  text-align: center;
  margin-bottom: 2rem;
`;

const Divider = styled.div`
  display: flex;
  align-items: center;
  margin: 1.5rem 0;
  color: ${({ theme }) => theme.colors.textSecondary};

  &::before, &::after {
    content: '';
    flex: 1;
    border-bottom: 1px solid ${({ theme }) => theme.colors.border};
  }

  &::before {
    margin-right: 1rem;
  }

  &::after {
    margin-left: 1rem;
  }
`;

const ManualEntryButton = styled.button`
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 1rem;
  padding: 2rem;
  border: 2px dashed ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.radii.lg};
  background-color: ${({ theme }) => theme.colors.card};
  cursor: pointer;
  transition: all 0.2s ease;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 1rem;

  &:hover {
    border-color: ${({ theme }) => theme.colors.primary};
    background-color: ${({ theme }) => theme.colors.background};
  }
`;

const Home = () => {
  const navigate = useNavigate();

  const handleImageUpload = (file) => {
   console.log('handleImageUpload called with:', file);
   // In a real app, you would upload the image to your backend
   console.log('Image uploaded:', file);
   navigate('/loading', { state: { images: file } });
  };

  const handleFormSubmit = (formData) => {
    console.log('Form submitted:', formData);
    navigate('/results', { state: { vehicleDetails: formData } });
  };

  return (
    <>
      <Header />
      <HomeContainer>
        <Title>Get Your Vehicle's Value</Title>
        <Subtitle>Upload a photo or enter details manually for an instant valuation</Subtitle>

        <ImageUpload onImageUpload={handleImageUpload} />
        <Divider>or</Divider>
        <ManualEntryButton onClick={() => {
          console.log('Manual entry button clicked');
          navigate('/vehicle-form');
        }}>
          <EditIcon width={48} height={48} fill="#2563eb" />
          Enter Details Manually
        </ManualEntryButton>

        <VehicleForm onSubmit={handleFormSubmit} />
      </HomeContainer>
    </>
  );
};

export default Home;
