import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import { AppContext } from '../context/AppContext';
import { ReactComponent as ThemeIcon } from '../assets/icons/settings.svg';
import { ReactComponent as Logo } from '../assets/images/logo.svg';
import { ReactComponent as HistoryIcon } from '../assets/icons/history.svg';
import { ReactComponent as SettingsIcon } from '../assets/icons/settings.svg';
import { ReactComponent as SaveIcon } from '../assets/icons/save.svg';

const HeaderContainer = styled.header`
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 1rem 2rem;
  background-color: ${({ theme }) => theme.colors.card};
  box-shadow: ${({ theme }) => theme.shadows.sm};
`;

const LogoContainer = styled.div`
  cursor: pointer;
`;

const NavItems = styled.div`
  display: flex;
  gap: 1.5rem;
`;

const NavIcon = styled.div`
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  border-radius: 50%;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primary}20;
  }
`;

const ThemeToggleButton = styled.button`
  background: none;
  border: none;
  cursor: pointer;
  color: ${({ theme }) => theme.colors.textPrimary}; /* Adjust color as needed */
  font-size: 1rem;
  display: flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem;
  border-radius: ${({ theme }) => theme.radii.md};
  transition: background-color 0.3s ease;

  &:hover {
    background-color: ${({ theme }) => theme.colors.backgroundSecondary};
  }
`;

const Header = () => {
  const { theme, toggleTheme } = React.useContext(AppContext);
  const [isHistoryOpen, setIsHistoryOpen] = React.useState(false);
  const [isSettingsOpen, setIsSettingsOpen] = React.useState(false);
  const navigate = useNavigate();

  const handleHistoryClick = () => {
    if (isHistoryOpen) {
      navigate('/'); // Navigate to home if already open
    } else {
      navigate('/history');
    }
    setIsHistoryOpen(!isHistoryOpen);
  };

  const handleSettingsClick = () => {
    if (isSettingsOpen) {
      navigate('/'); // Navigate to home if already open
    } else {
      navigate('/settings');
    }
    setIsSettingsOpen(!isSettingsOpen);
  };

  return (
    <HeaderContainer>
      <LogoContainer onClick={() => navigate('/')}>
        <Logo width={120} height={40} />
      </LogoContainer>
      <NavItems>
        <NavIcon onClick={handleHistoryClick}>
          <HistoryIcon width={20} height={20} fill="currentColor" />
        </NavIcon>
        <NavIcon onClick={() => navigate('/saved-reports')}>
          <SaveIcon width={20} height={20} fill="currentColor" />
        </NavIcon>
        <NavIcon onClick={handleSettingsClick}>
          <SettingsIcon width={20} height={20} fill="currentColor" />
        </NavIcon>
      </NavItems>
      <ThemeToggleButton onClick={() => toggleTheme(theme === 'light' ? 'dark' : 'light')}>
        <ThemeIcon width={20} height={20} fill="currentColor" />
        <span>{theme === 'light' ? 'Light' : 'Dark'} Mode</span>
      </ThemeToggleButton>
    </HeaderContainer>
  );
};

export default Header;
