import React, { useState } from 'react';
import styled from 'styled-components';

const FormContainer = styled.div`
  background-color: ${({ theme }) => theme.colors.card};
  padding: 2rem;
  border-radius: ${({ theme }) => theme.radii.lg};
  box-shadow: ${({ theme }) => theme.shadows.md};
`;

const FormGroup = styled.div`
  margin-bottom: 1.5rem;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 0.5rem;
  color: ${({ theme }) => theme.colors.textSecondary};
  font-size: 0.875rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.radii.md};
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.text};
`;

const Select = styled.select`
  width: 100%;
  padding: 0.75rem;
  border: 1px solid ${({ theme }) => theme.colors.border};
  border-radius: ${({ theme }) => theme.radii.md};
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.text};
`;

const Button = styled.button`
  width: 100%;
  padding: 0.75rem;
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${({ theme }) => theme.radii.md};
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

// Mock data for dropdowns
const MAKES = ['Toyota', 'Honda', 'Ford', 'Chevrolet', 'BMW', 'Mercedes-Benz'];
const MODELS = {
  Toyota: ['Camry', 'Corolla', 'RAV4', 'Highlander', 'Prius'],
  Honda: ['Civic', 'Accord', 'CR-V', 'Pilot', 'Odyssey'],
  Ford: ['F-150', 'Mustang', 'Explorer', 'Escape', 'Focus'],
  Chevrolet: ['Silverado', 'Equinox', 'Malibu', 'Tahoe', 'Camaro'],
  BMW: ['3 Series', '5 Series', 'X3', 'X5', '7 Series'],
  'Mercedes-Benz': ['C-Class', 'E-Class', 'GLC', 'GLE', 'S-Class'],
};

const YEARS = Array.from({ length: 25 }, (_, i) => 2023 - i);

const VehicleForm = ({ onSubmit }) => {
  const [formData, setFormData] = useState({
    make: '',
    model: '',
    year: '',
    mileage: '',
  });

  const [availableModels, setAvailableModels] = useState([]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));

    if (name === 'make') {
      setAvailableModels(MODELS[value] || []);
      setFormData(prev => ({ ...prev, model: '' }));
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <FormContainer>
      <form onSubmit={handleSubmit}>
        <FormGroup>
          <Label>Make</Label>
          <Select 
            name="make" 
            value={formData.make} 
            onChange={handleChange}
            required
          >
            <option value="">Select Make</option>
            {MAKES.map(make => (
              <option key={make} value={make}>{make}</option>
            ))}
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Model</Label>
          <Select 
            name="model" 
            value={formData.model} 
            onChange={handleChange}
            required
            disabled={!formData.make}
          >
            <option value="">Select Model</option>
            {availableModels.map(model => (
              <option key={model} value={model}>{model}</option>
            ))}
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Year</Label>
          <Select 
            name="year" 
            value={formData.year} 
            onChange={handleChange}
            required
          >
            <option value="">Select Year</option>
            {YEARS.map(year => (
              <option key={year} value={year}>{year}</option>
            ))}
          </Select>
        </FormGroup>

        <FormGroup>
          <Label>Mileage</Label>
          <Input 
            type="number" 
            name="mileage" 
            value={formData.mileage} 
            onChange={handleChange}
            placeholder="Enter mileage"
            required
          />
        </FormGroup>

        <Button type="submit">Get Valuation</Button>
      </form>
    </FormContainer>
  );
};

export default VehicleForm;
