import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import ReportModal from '../components/ReportModal';

const ReportsContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const ReportItem = styled.div`
  background-color: ${({ theme }) => theme.colors.card};
  padding: 1rem;
  border-radius: ${({ theme }) => theme.radii.md};
  box-shadow: ${({ theme }) => theme.shadows.md};
  display: flex;
  align-items: center;
  justify-content: space-between;
  cursor: pointer;
`;

const ReportCheckbox = styled.input`
  margin-right: 1rem;
`;

const SavedReports = () => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);
  const [selectedReports, setSelectedReports] = useState([]);

  useEffect(() => {
    const savedReports = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key.startsWith('report-')) {
        const report = localStorage.getItem(key);
        savedReports.push({ id: key, data: report });
      }
    }
    setReports(savedReports);
  }, []);

  const handleReportClick = (report) => {
    setSelectedReport(report);
  };

  const handleCloseModal = () => {
    setSelectedReport(null);
  };

  const handleSelectReport = (reportId) => {
    setSelectedReports((prevSelected) => {
      if (prevSelected.includes(reportId)) {
        return prevSelected.filter((id) => id !== reportId);
      } else {
        return [...prevSelected, reportId];
      }
    });
  };

  const handleSelectAll = () => {
    if (selectedReports.length === reports.length) {
      setSelectedReports([]);
    } else {
      setSelectedReports(reports.map((report) => report.id));
    }
  };

  const handleDeleteSelected = () => {
    selectedReports.forEach((reportId) => {
      localStorage.removeItem(reportId);
    });
    setReports(reports.filter((report) => !selectedReports.includes(report.id)));
    setSelectedReports([]);
  };

  return (
    <>
      <Header />
      <ReportsContainer>
        <h1>Saved Reports</h1>
        <label>
          <input
            type="checkbox"
            onChange={handleSelectAll}
            checked={selectedReports.length === reports.length}
          />
          Select All
        </label>
        {reports.map((report, index) => (
          <ReportItem key={index} onClick={() => handleReportClick(report.data)}>
            <div>
              <h2>Report {index + 1}</h2>
            </div>
            <ReportCheckbox
              type="checkbox"
              value={report.id}
              checked={selectedReports.includes(report.id)}
              onChange={() => handleSelectReport(report.id)}
            />
          </ReportItem>
        ))}
        <button onClick={handleDeleteSelected}>Delete Selected</button>
      </ReportsContainer>
      {selectedReport && (
        <ReportModal report={selectedReport} onClose={handleCloseModal} />
      )}
    </>
  );
};

export default SavedReports;
