import React, { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import styled, { keyframes } from 'styled-components';
import { useAppContext } from '../context/AppContext';

const fadeIn = keyframes`
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
`;

const LoadingContainer = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  height: 100vh;
  background-color: ${({ theme }) => theme.colors.background};
  color: ${({ theme }) => theme.colors.textPrimary};
  animation: ${fadeIn} 0.5s ease-in-out;
`;

const LoadingMessage = styled.h2`
  font-size: 2rem;
  margin-bottom: 1rem;
`;

const Loader = styled.div`
  border: 8px solid ${({ theme }) => theme.colors.border};
  border-top: 8px solid ${({ theme }) => theme.colors.primary};
  border-radius: 50%;
  width: 60px;
  height: 60px;
  animation: spin 1s linear infinite;

  @keyframes spin {
    0% { transform: rotate(0deg); }
    100% { transform: rotate(360deg); }
  }
`;

const Loading = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { estimateValue } = useAppContext();
  const vehicleDetails = location.state?.vehicleDetails || null;
  const nextRoute = location.state?.nextRoute;


  useEffect(() => {
    console.log('Loading: nextRoute from state:', nextRoute); // Log nextRoute
    console.log('Loading: vehicleDetails from state:', vehicleDetails); // Log vehicleDetails

    if (vehicleDetails) {
      console.log('Loading: Starting value estimation'); // Log action
      estimateValue(vehicleDetails)
        .then(valuationResult => {
          console.log('Loading: Value estimation successful, navigating to:', nextRoute); // Log navigation path
          navigate(nextRoute, { state: { valuationResult: valuationResult, vehicleDetails: vehicleDetails } });
        })
        .catch(error => {
          console.error("Value estimation failed:", error);
          navigate('/error', { state: { message: "Failed to estimate vehicle value." } });
        });
    }
  }, [location, navigate, estimateValue, vehicleDetails, nextRoute]);

  return (
    <LoadingContainer>
      <LoadingMessage>Estimating Vehicle Value...</LoadingMessage>
      <Loader />
    </LoadingContainer>
  );
};

export default Loading;
