import { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { useAppContext } from '../context/AppContext';
import styled from 'styled-components';

const LoadingContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
  font-size: 1.5rem;
`;

function Loading() {
  const navigate = useNavigate();
  const location = useLocation();
  const { analyzeImage, estimateValue } = useAppContext();

  useEffect(() => {
    const images = location.state?.images || [];
    const vehicleDetails = location.state?.vehicleDetails || null;

    if (images.length > 0) {
      // Analyze Image
      const image = images[0]; // Assuming single image upload
      analyzeImage(image)
        .then(() => {
          navigate('/analyze');
        })
        .catch((error) => {
          console.error("Image analysis error:", error);
          navigate('/'); // Navigate back to home on error
        });
    } else if (vehicleDetails) {
      // Estimate Value
      estimateValue(vehicleDetails)
        .then(() => {
          navigate('/results');
        })
        .catch((error) => {
          console.error("Value estimation error:", error);
          navigate('/'); // Navigate back to home on error
        });
    } else {
      navigate('/');
    }
  }, [location, navigate, analyzeImage, estimateValue]);

  return (
    <LoadingContainer>
      Loading...
    </LoadingContainer>
  );
}

export default Loading;
