import React from 'react';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';
import ImageUpload from '../components/ImageUpload';
import VehicleForm from './VehicleForm';
import Header from '../components/Header';

const HomeContainer = styled.div`
  width: 100%; /* Make HomeContainer full width again */
  /* max-width: 1200px; Increased max-width to allow wider content - Removed max-width from HomeContainer */
  /* width: 70%; Set width to 70% for "70% centered" layout - Removed width constraint */
  /* max-width: 1400px; Increased max-width to allow wider content - Removed max-width */
  margin: 2rem auto;
  padding: 3rem 2rem; /* Adjust padding to be similar to previous */
  display: flex; /* Revert to flexbox for simpler centering */
  flex-direction: column;
  /* align-items: center; Center items horizontally - Removed align-items from HomeContainer */
  gap: 2.5rem; /* Row gap - adjust if needed */
  background-color: ${({ theme }) => theme.colors.surface};
  border-radius: ${({ theme }) => theme.radii.lg};
  box-shadow: ${({ theme }) => theme.shadows.md};
  text-align: center; /* Center text content - ensure text is centered */
`;

const Title = styled.h1`
  color: ${({ theme }) => theme.colors.textPrimary}; /* Use primary text color from theme */
  font-size: 2.5rem; /* Larger font size */
  text-align: center; /* Center align the title */
  margin-bottom: 1.5rem; /* Increased margin bottom */
`;

const Subtitle = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  text-align: center; /* Center align the subtitle */
  margin-bottom: 2rem;
`;


const Home = () => {
  const navigate = useNavigate();

  const handleImageUpload = (file) => {
   console.log('handleImageUpload called with:', file);
   // In a real app, you would upload the image to your backend
   console.log('Image uploaded:', file);
   navigate('/loading', { state: { images: file } });
  };

  const handleFormSubmit = (formData) => {
    console.log('Form submitted:', formData);
    navigate('/results', { state: { vehicleDetails: formData } });
  };

  return (
    <>
      <Header />
      <HomeContainer>
        <div> {/* Container for text content */}
          <Title>Get Your Vehicle's Value</Title>
          <Subtitle>Upload a photo or enter details manually for an instant valuation</Subtitle>
        </div>

        <div className="form-section"> {/* Wrapper div for ImageUpload and VehicleForm */}
          <ImageUpload onImageUpload={handleImageUpload} />
          <div style={{marginTop: '2rem'}}> {/* Add margin to separate form */}
            <VehicleForm onSubmit={handleFormSubmit} style={{width: '70%', maxWidth: '1200px', margin: '0 auto', alignSelf: 'center'}} /> {/* Inline styles to make VehicleForm 70% width and centered */}
          </div>
        </div>
      </HomeContainer>

      {/* Styles for FormSection */}
      <style>
        {`
          .form-section {
            display: flex;
            flex-direction: column;
            align-items: center; /* Center content in FormSection */
            gap: 2.5rem; /* Gap between ImageUpload and VehicleForm */
            width: 70%; /* Limit width of FormSection */
            margin: 0 auto; /* Center FormSection horizontally */
          }
        `}
      </style>
    </>
  );
};

export default Home;
