import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import Header from '../components/Header';
import ReportModal from '../components/ReportModal';

const ReportsContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 1rem;
`;

const ReportItem = styled.div`
  background-color: ${({ theme }) => theme.colors.card};
  padding: 1rem;
  border-radius: ${({ theme }) => theme.radii.md};
  box-shadow: ${({ theme }) => theme.shadows.md};
  cursor: pointer;
`;

const SavedReports = () => {
  const [reports, setReports] = useState([]);
  const [selectedReport, setSelectedReport] = useState(null);

  useEffect(() => {
    const savedReports = [];
    for (let i = 0; i < localStorage.length; i++) {
      const key = localStorage.key(i);
      if (key.startsWith('report-')) {
        const report = JSON.parse(localStorage.getItem(key));
        savedReports.push(report);
      }
    }
    setReports(savedReports);
  }, []);

  const handleReportClick = (report) => {
    setSelectedReport(report);
  };

  const handleCloseModal = () => {
    setSelectedReport(null);
  };

  return (
    <>
      <Header />
      <ReportsContainer>
        <h1>Saved Reports</h1>
        {reports.map((report, index) => (
          <ReportItem key={index} onClick={() => handleReportClick(report)}>
            <h2>{report.vehicle.year} {report.vehicle.make} {report.vehicle.model}</h2>
            <p>Valuation: {report.valuation.price}</p>
          </ReportItem>
        ))}
      </ReportsContainer>
      {selectedReport && (
        <ReportModal report={selectedReport} onClose={handleCloseModal} />
      )}
    </>
  );
};

export default SavedReports;
