import React, { useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import ImageAnalysisLoading from '../components/ImageAnalysisLoading';
import FinalLoading from '../components/FinalLoading';

function Loading() {
  const navigate = useNavigate();
  const location = useLocation();
  const vehicleDetails = location.state?.vehicleDetails || {};
  const images = location.state?.images || [];
  const loadingType = location.state?.loadingType || 'initial';

  useEffect(() => {
    // Simulate loading process
    let destination = '/analyze';
    if (loadingType === 'final') {
      destination = '/results';
    }

    setTimeout(() => {
      navigate(destination, { state: { vehicleDetails, images } });
    }, 2000);
  }, [navigate, vehicleDetails, images, loadingType]);

  return (
    <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
      {loadingType === 'final' ? <FinalLoading /> : <ImageAnalysisLoading />}
    </div>
  );
}

export default Loading;
