import React, { useRef, useState } from 'react';
import html2canvas from 'html2canvas';
import styled, { keyframes } from 'styled-components';
import { useLocation, useNavigate } from 'react-router-dom';
import { ReactComponent as ShareIcon } from '../assets/icons/share.svg';
import { ReactComponent as RestartIcon } from '../assets/icons/restart.svg';
import { ReactComponent as SaveIcon } from '../assets/icons/save.svg';
import Header from '../components/Header';
import PriceDisplay from '../components/PriceDisplay';
import PriceBreakdown from '../components/PriceBreakdown';
import ValueRange from '../components/ValueRange';

const ResultsContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
  display: flex;
  flex-direction: column;
  gap: 2rem;
`;

const ResultsHeader = styled.div`
  text-align: center;
  margin-bottom: 1rem;
`;

const VehicleTitle = styled.h2`
  color: ${({ theme }) => theme.colors.text};
  margin-bottom: 0.5rem;
`;

const VehicleSubtitle = styled.p`
  color: ${({ theme }) => theme.colors.textSecondary};
  margin: 0;
`;

const ButtonBar = styled.div`
  display: flex;
  justify-content: space-around;
  margin-top: 2rem;
`;

const Button = styled.button`
  display: flex;
  flex-direction: row;
  align-items: center;
  justify-content: center;
  gap: 0.5rem;
  padding: 0.75rem 1.5rem;
  background-color: ${({ theme }) => theme.colors.primary};
  color: white;
  border: none;
  border-radius: ${({ theme }) => theme.radii.md};
  font-weight: 600;
  cursor: pointer;
  transition: all 0.2s ease;

  &:hover {
    background-color: ${({ theme }) => theme.colors.primaryDark};
  }
`;

const ShareButton = styled(Button)`
  flex-direction: row;
`;

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const fadeOut = keyframes`
  from {
    opacity: 1;
    transform: translateY(0);
  }
  to {
    opacity: 0;
    transform: translateY(-20px);
  }
`;

const colorShift = keyframes`
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
`;

const colorFlash = keyframes`
  0%, 100% {
    background-color: ${({ theme }) => theme.colors.success};
  }
  50% {
    background-color: ${({ theme }) => theme.colors.successLight};
  }
`;

const SaveConfirmation = styled.div`
  position: absolute;
  bottom: 110px;
  right: 10px;
  background: linear-gradient(90deg, ${({ theme }) => theme.colors.success}, ${({ theme }) => theme.colors.primary});
  background-size: 200% 200%;
  color: white;
  padding: 0.5rem 1rem;
  border-radius: ${({ theme }) => theme.radii.md};
  animation: ${fadeIn} 0.3s ease-in-out, ${fadeOut} 0.3s ease-in-out 1.7s, ${colorShift} 2s linear infinite, ${colorFlash} 2s ease-in-out;
  z-index: 10;
  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.2);
`;

const ValuationResults = () => {
  const resultsRef = useRef(null);
  const reportDetailsRef = useRef(null);
  const [isSaveConfirmed, setIsSaveConfirmed] = useState(false);
  const location = useLocation();
  const navigate = useNavigate();
  const vehicle = location.state?.vehicleDetails || {
    make: 'Toyota',
    model: 'Camry',
    year: '2020',
    mileage: '45000',
  };

  const valuation = {
    price: '$24,500',
    breakdown: [
      { label: 'Base Market Value', value: '$26,000' },
      { label: 'Mileage Adjustment', value: '-$1,200', isNegative: true },
      { label: 'Condition Adjustment', value: '-$300', isNegative: true },
    ],
    range: {
      low: '$22,000',
      high: '$27,000',
      current: '$24,500',
    },
  };

  const handleStartNew = () => {
    navigate('/');
  };

 const handleSave = async () => {
    if (!resultsRef.current) return;

    try {
      const canvas = await html2canvas(reportDetailsRef.current);
      const screenshotDataURL = canvas.toDataURL('image/png');
      const reportId = Date.now();
      localStorage.setItem(`report-${reportId}`, screenshotDataURL);
      console.log('Valuation report saved:', reportId);
      setIsSaveConfirmed(true);
      setTimeout(() => {
        setIsSaveConfirmed(false);
      }, 2000);
    } catch (error) {
      console.error('Error capturing screenshot:', error);
    }
  };

  const handleShare = () => {
    console.log('Sharing valuation report');
    // Implement share functionality
  };

  return (
    <>
      <Header />
      {isSaveConfirmed && <SaveConfirmation>Report Saved!</SaveConfirmation>}
      <ResultsContainer ref={resultsRef}>
        <div ref={reportDetailsRef}>
        <ResultsHeader>
          <VehicleTitle>{vehicle.year} {vehicle.make} {vehicle.model}</VehicleTitle>
          <VehicleSubtitle>{vehicle.mileage} miles</VehicleSubtitle>
        </ResultsHeader>

        <PriceDisplay price={valuation.price} />
        <ValueRange 
          low={valuation.range.low} 
          high={valuation.range.high} 
          current={valuation.range.current} 
        />
        <PriceBreakdown items={valuation.breakdown} />

        <ButtonBar>
          <Button onClick={handleStartNew}>
            <RestartIcon width={20} height={20} fill="white" />
            <span>New Estimate</span>
          </Button>
          <Button onClick={handleSave}>
            <SaveIcon width={20} height={20} fill="white" />
            <span>Save Report</span>
          </Button>
          <ShareButton onClick={handleShare}>
            <ShareIcon width={20} height={20} fill="white" />
            <span>Share Report</span>
          </ShareButton>
        </ButtonBar>
      </div>
      </ResultsContainer>
    </>
  );
};

export default ValuationResults;
